package operatorsUse;

public class OperatorsUse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 3;
		int b = 5;
	   System.out.println(a|b);
	   System.out.println(a&b);
	   System.out.println(a==b);
	   System.out.println(a>b);
	   System.out.println(a>>1);
	   System.out.println(a<<1);
	   System.out.println(b%a);
	   

	}

}
